import React, { useState } from "react";
import axios from "axios";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { Button, FormControlLabel, Checkbox } from "@material-ui/core";

export const CheckoutForm = ({ precio, notas, direccion }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [seguro, setSeguro] = useState(true);
  const [total, seTotal] = useState(precio + 60);

  const handlePago = async (e) => {
    e.preventDefault();

    console.log(notas);
    console.log(direccion);

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (!error) {
      console.log(paymentMethod);
      const { id } = paymentMethod;
      const { data } = await axios.post(
        "https://us-central1-mandadero-f2403.cloudfunctions.net/checkout",
        {
          amount: total * 100,
          id,
        }
      );
      console.log(data);
    }
  };

  const handleChange = (event) => {
    setSeguro(event.target.checked);
    const seguro_precio = event.target.checked ? 60 : -60;
    seTotal((total) => total + seguro_precio);
  };

  return (
    <form onSubmit={handlePago}>
      <Backdrop className={classes.backdrop} open={open} onClick={handleClose}>
        <CircularProgress color="inherit" />
      </Backdrop>
      <CardElement
        style={{ padding: "20px 0" }}
        options={{
          style: {
            base: {
              fontSize: "16px",
              color: "#424770",
              "::placeholder": {
                color: "#aab7c4",
              },
            },
            invalid: {
              color: "#9e2146",
            },
          },
        }}
      />
      <div style={{ margin: "30px 0" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            fontSize: "14px",
          }}
        >
          <p style={{ margin: "0 25px 0 0" }}>Subtotal</p>
          <p>
            ${precio}.00<span style={{ fontSize: "10px" }}>MX</span>
          </p>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            fontSize: "14px",
            alignItems: "center",
          }}
        >
          <FormControlLabel
            style={{ fontSize: "14px", margin: "0 25px 0 0" }}
            control={
              <Checkbox
                color="primary"
                checked={seguro}
                onChange={handleChange}
                name="seguro"
              />
            }
            label="¿Desea agregar el seguro?"
          />
          <p>
            $60.00<span style={{ fontSize: "10px" }}>MX</span>
          </p>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            fontSize: "14px",
          }}
        >
          <p style={{ margin: "0 25px 0 0" }}>Total</p>
          <p>
            ${total}.00<span style={{ fontSize: "10px" }}>MX</span>
          </p>
        </div>
      </div>

      <Button
        style={{ margin: "0 auto", display: "block" }}
        variant="contained"
        color="primary"
        type="submit"
      >
        Pagar
      </Button>
    </form>
  );
};
