import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import DateFnsUtils from "@date-io/date-fns";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { useSelector } from "react-redux";
import Alert from "@material-ui/lab/Alert";
import IconButton from "@material-ui/core/IconButton";
import Collapse from "@material-ui/core/Collapse";
import CloseIcon from "@material-ui/icons/Close";
import ViewListIcon from "@material-ui/icons/ViewList";
import ViewModuleIcon from "@material-ui/icons/ViewModule";
import ViewQuiltIcon from "@material-ui/icons/ViewQuilt";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBoxOpen } from "@fortawesome/free-solid-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";

import { useForm } from "../../../hooks/useForm";

export const EnvioScreen = () => {
  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const style_btn = {
    margin: "25px 0",
  };

  const [{ ine, tipoViaje, equipajePeso }, handleChange, reset] = useForm({
    name: "",
    tipoViaje: "",
    equipajePeso: "",
    ine: "",
  });

  const [fecha, setFecha] = useState(Date.now());
  const [direccion, setDireccion] = useState({
    dirSalida: "",
    dirDestino: "",
  });
  const [checkCarta, setCheckCarta] = useState(true);
  const [alert, setAlert] = useState(false);

  const { uid } = useSelector((state) => state.auth);

  const handleChangeFecha = (fecha) => {
    setFecha(fecha);
  };

  const searchOptions = {
    types: ["(cities)"],
    componentRestrictions: {
      country: "mx",
    },
  };

  const handleChangeDireccion = (address, idx) => {
    if (idx === 1) {
      setDireccion({ ...direccion, dirSalida: address.value.description });
    } else {
      setDireccion({ ...direccion, dirDestino: address.value.description });
    }
  };

  const handleChangeCheck = (event) => {
    setCheckCarta(event.target.checked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const body = {
      salida: direccion.dirSalida,
      destino: direccion.dirDestino,
      fecha,
      tipoViaje,
      equipajePeso,
      checkCarta,
      ine,
      viajeroID: uid,
    };
    setAlert(true);
  };

  const [view, setView] = useState("list");

  const handleChangeToggle = (event, nextView) => {
    setView(nextView);
  };

  return (
    <>
      <div className="auth__container">
        <h1>¿A dónde quieres envíar?</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt, 1),
                placeholder: "Salida",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt, 2),
                placeholder: "Destino",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              style={style_input}
              disableToolbar
              variant="inline"
              format="dd/MM/yyyy"
              margin="normal"
              label="Dia del envio"
              value={fecha}
              onChange={handleChangeFecha}
            />
          </MuiPickersUtilsProvider>
          <FormControlLabel
            style={{ color: "black", marginTop: "15px", marginBottom: "15px" }}
            control={
              <Checkbox
                checked={checkCarta}
                onChange={handleChangeCheck}
                name="checkCarta"
                color="primary"
              />
            }
            label="Carta responsiva"
          />
          <ToggleButtonGroup
            orientation="vertical"
            value={view}
            exclusive
            onChange={handleChangeToggle}
          >
            <ToggleButton value="list" aria-label="list">
              <div>
                <FontAwesomeIcon icon={faEnvelope} />
                <p>Sobre | Documento</p>
                <strong>1KG</strong>
              </div>
            </ToggleButton>
            <ToggleButton value="module" aria-label="module">
              <div>
                <p>Caja mediana</p>
                <FontAwesomeIcon icon={faBoxOpen} />
              </div>
            </ToggleButton>
            <ToggleButton value="quilt" aria-label="quilt">
              <div>
                <p>Caja grande</p>
                <FontAwesomeIcon icon={faBoxOpen} />
              </div>
            </ToggleButton>
          </ToggleButtonGroup>
          <Button
            disabled={!checkCarta}
            style={style_btn}
            type="submit"
            variant="outlined"
          >
            Solicitar Envio
          </Button>
        </form>
      </div>
    </>
  );
};
