import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { useForm } from "../../hooks/useForm";
import DateFnsUtils from "@date-io/date-fns";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import { components } from "react-select";

export const MandaderoScreen = () => {
  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const { Option } = components;
  const IconOption = (props) => (
    <Option {...props}>
      <img
        src={require("./" + props.data.icon)}
        style={{ width: 36 }}
        alt={props.data.label}
      />
      {props.data.label}
    </Option>
  );

  const [{ name }, handleChange, reset] = useForm({
    name: "",
  });

  const [fecha, setFecha] = useState(Date.now());
  const [direccion, setDireccion] = useState("");

  const handleChangeFecha = (fecha) => {
    setFecha(fecha);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const searchOptions = {
    types: ["(cities)"],
    componentRestrictions: {
      country: "mx",
    },
  };

  const handleChangeDireccion = (address) => {
    console.log(address);
    setDireccion(address);
  };

  return (
    <>
      <div className="auth__container">
        <h1>Publicar Viaje</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <div style={{ width: "100%" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                placeholder: "Direccion",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              style={style_input}
              disableToolbar
              variant="inline"
              format="dd/MM/yyyy"
              margin="normal"
              label="Dia del viaje"
              value={fecha}
              onChange={handleChangeFecha}
            />
          </MuiPickersUtilsProvider>
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
        </form>
      </div>
    </>
  );
};
