import { db } from "../../firebase/firebaseConfig";

export const getViaje = ({
  dirSalida,
  dirDestino,
  fechaEnvio,
  pesoPaquete,
}) => {
  console.log(dirSalida);
  console.log(dirDestino);
  console.log(fechaEnvio);
  console.log(pesoPaquete);

  const viajesRef = db.collection("viajes");
  viajesRef
    .where("salida", "==", dirSalida)
    .where("destino", "==", dirDestino)
    .where("fechaViaje", "==", fechaEnvio)
    .where("equipajePeso", ">=", pesoPaquete)
    .get()
    .then((viajes) => {
      console.log(viajes);

      if (!viajes.empty) {
        return true;
        // viajes.docs.forEach(async (viaje) => {
        //   const equipajePeso = viaje.data()["equipajePeso"] - pesoPaquete;
        //   const idViaje = viaje.id;
        //   await updateViaje(idViaje, equipajePeso);
        //   await setEnvioViaje({ pesoPaquete, idViaje });
        // });
      }
      return false;
    });
};

const updateViaje = (id, peso) => {
  return db.collection("viajes").doc(id).update({ equipajePeso: peso });
};

const setEnvioViaje = (body) => {
  return db.collection("envios").doc().set(body);
};
