import { db } from "../../firebase/firebaseConfig";

export const getEnvios = async (id) => {
  const envios = await db
    .collection("envios")
    .where("id_cliente", "==", id)
    .get()
    .then((envios) => {
      let enviosData = [];
      if (!envios.empty) {
        envios.docs.forEach((envio) => {
          viajesData.push({
            ...envio.data(),
            id: envio.id,
          });
        });
      }
      return enviosData;
    });
  return envios;
};
