import { db, storage } from "../../firebase/firebaseConfig";
import { types } from "../../types/types";

export const saveFile = async (file) => {
  const uploadTask = storage.ref(
    `envios_evidencia/${Date.now() + "-" + file.name}`
  );
  await uploadTask.put(file);
  const urlImg = await uploadTask.getDownloadURL();
  return urlImg;
};

export const saveCheckout = async ({
  notas,
  direccion,
  total,
  urlImg,
  viaje,
  auth,
}) => {
  const id_envio = `${viaje.origen.substr(0, 4)}${viaje.destino.substr(
    0,
    4
  )}${Date.now()}`;

  console.log(id_envio);
  console.log(viaje);
  console.log(auth);
};

const updateViaje = (id, peso) => {
  return db.collection("viajes").doc(id).update({ equipajePeso: peso });
};

const setEnvioViaje = (body) => {
  return db.collection("envios").doc().set(body);
};
