import { db } from "../../firebase/firebaseConfig";

export const getViaje = (dirSalida, dirDestino, fecha, pesoPaquete) => {
  const viajesRef = db.collection("vaijes");
  viajesRef
    .where("salida", "==", "dirSalida")
    .where("destino", "==", "dirDestino")
    .where("fecha", "==", "fecha")
    .get()
    .then((viajes) => {
      console.log(viajes);
    });
};
