import React, { useEffect, useState } from "react";
import {
  Button,
  Checkbox,
  FormControlLabel,
  Backdrop,
  CircularProgress,
  List,
  ListItem,
  ListItemAvatar,
  ListItemSecondaryAction,
  IconButton,
  Avatar,
  Popover,
  Chip,
} from "@material-ui/core";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import { format } from "date-fns";

import CommuteIcon from "@material-ui/icons/Commute";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

import { Header } from "../Header";

export const BusquedaScreen = () => {
  const viajes = useSelector((state) => state.busqueda);
  const history = useHistory();
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const id = open ? "simple-popover" : undefined;

  const [notas, setNotas] = useState("");
  const [hora, setHora] = useState(Date.now());

  useEffect(() => {
    if (viajes.length === 0) {
      history.push("/envio");
    }
  }, [viajes]);

  const setDireccion = (direccion) => {
    const newDir = direccion.split(",");
    return `${newDir[0]},${newDir[1]}`;
  };

  const handleClick = (event, note) => {
    setAnchorEl(event.currentTarget);
    setNotas(note);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleChangeHora = (hora) => {
    console.log(hora);
  };

  return (
    <>
      <Header />
      <div className="auth__container" style={{ marginTop: "20px" }}>
        <h1>Busqueda</h1>
        <div>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardTimePicker
              margin="normal"
              label=" Hora de llegada"
              value={hora}
              onChange={handleChangeHora}
            />
          </MuiPickersUtilsProvider>
          <List style={{ width: "100%" }}>
            {viajes.map((viaje) => (
              <div style={{ margin: "10px 0" }} key={viaje.id}>
                <ListItem button style={{ margin: "15px 0" }}>
                  <ListItemAvatar>
                    <Avatar>
                      <CommuteIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <div style={{ display: "flex", margin: "10px" }}>
                    <div style={{ margin: "0 8px" }}>
                      <p style={{ margin: "0 0 5px 0" }}>
                        {viaje.horaViaje} hrs {setDireccion(viaje.salida)}
                      </p>
                      <p style={{ margin: "5px 0 0 0" }}>
                        {viaje.hora_llegada} hrs {setDireccion(viaje.destino)}
                      </p>
                    </div>
                    <div style={{ display: "flex", alignItems: "flex-end" }}>
                      <p>${viaje.precio}</p>
                    </div>
                  </div>
                </ListItem>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "flex-end",
                  }}
                >
                  <Chip
                    style={{ margin: "0 10px" }}
                    onClick={(event) => handleClick(event, viaje.notas)}
                    label="Ver detalles"
                    variant="outlined"
                  />
                  <Chip
                    style={{ borderColor: "blue" }}
                    label={`Quedan ${viaje.equipajePeso}kg`}
                    variant="outlined"
                  />
                </div>
              </div>
            ))}
          </List>
          <Popover
            id={id}
            open={open}
            anchorEl={anchorEl}
            onClose={handleClose}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "center",
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "center",
            }}
          >
            <div style={{ padding: "20px" }}>{notas}</div>
          </Popover>
        </div>
      </div>
    </>
  );
};
