import React from "react";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";

import { Header } from "../Header";

export const CheckoutScreen = () => {
  const searchOptions = {
    componentRestrictions: {
      country: "mx",
    },
  };

  const handleChangeDireccion = (address) => {
    setDireccion({ ...direccion, dirSalida: address.value.description });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const fechaEnvio = format(fecha, "dd/MM/yy");
    const { dirSalida, dirDestino } = direccion;
    const body = {
      dirSalida,
      dirDestino,
      pesoPaquete,
      fechaEnvio,
    };
    setopen(true);

    const responseViaje = await getViaje(body);
    setopen(false);

    if (responseViaje) {
      history.push("/checkout");
    }
  };
  return (
    <>
      <Header />
      <div className="auth__container" style={{ marginTop: "20px" }}>
        <h1>Pago</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt, 1),
                placeholder: "Salida",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
        </form>
      </div>
    </>
  );
};
