import { types } from "../types/types";
export const busquedaReducer = (state = [], action) => {
  switch (action.type) {
    case types.busqueda:
      return action.payload;

    case types.emptyBusqueda:
      return [];

    case types.selectBusqueda:
      return {
        equipajePeso,
        viajeroID,
        id,
        precio,
      };

    default:
      return state;
  }
};
