import React, { useRef, useState } from "react";
import DateFnsUtils from "@date-io/date-fns";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import {
  Button,
  Checkbox,
  FormControlLabel,
  Backdrop,
  CircularProgress,
  List,
  ListItem,
  ListItemAvatar,
  Avatar,
  ListItemText,
} from "@material-ui/core";
import FolderIcon from "@material-ui/icons/Folder";
import { ToggleButton, ToggleButtonGroup } from "@material-ui/lab";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBoxOpen,
  faEnvelope,
  faImage,
} from "@fortawesome/free-solid-svg-icons";
import { format } from "date-fns";
import { useHistory } from "react-router-dom";

import { Header } from "../Header";
import { getViaje } from "../../../actions/mandadero/envio";
import { useDispatch, useSelector } from "react-redux";

export const EnvioScreen = () => {
  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const style_btn = {
    margin: "25px 0",
  };

  const searchOptions = {
    types: ["(cities)"],
    componentRestrictions: {
      country: "mx",
    },
  };

  const [imgPaquete, setimgPaquete] = useState(null);
  const [fecha, setFecha] = useState(Date.now());
  const [direccion, setDireccion] = useState({
    dirSalida: "",
    dirDestino: "",
  });
  const [pesoPaquete, setPesoPaquete] = useState(1);
  const [checkCarta, setCheckCarta] = useState(true);
  const [open, setopen] = useState(false);
  const [viajes, setViajes] = useState([]);

  const refImage = useRef(null);

  const history = useHistory();

  const dispatch = useDispatch();

  const { busqueda } = useSelector((state) => state.busqueda);

  const handleChangeFecha = (fecha) => {
    setFecha(fecha);
  };

  const handleChangeDireccion = (address, idx) => {
    if (idx === 1) {
      setDireccion({ ...direccion, dirSalida: address.value.description });
    } else {
      setDireccion({ ...direccion, dirDestino: address.value.description });
    }
  };

  const handleChangeCheck = (event) => {
    setCheckCarta(event.target.checked);
  };

  const handleChangeToggle = (e, selectPeso) => {
    setPesoPaquete(selectPeso);
  };

  const handleChangeInputImg = (e) => {
    const reader = new FileReader();
    const file = e.target.files[0];

    reader.onload = (event) => {
      setimgPaquete(file);
      refImage.current.src = event.target.result;
    };

    reader.onerror = (event) => {
      console.log("File could not be read: " + event.target.error.code);
    };

    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const fechaEnvio = format(fecha, "dd/MM/yy");
    const { dirSalida, dirDestino } = direccion;
    const body = {
      dirSalida,
      dirDestino,
      pesoPaquete,
      fechaEnvio,
    };
    setopen(true);

    dispatch(getViaje(body));
    setopen(false);
  };

  return (
    <>
      <Backdrop open={open} style={{ zIndex: "9999", color: "white" }}>
        <CircularProgress color="inherit" />
      </Backdrop>
      <Header />
      <div className="auth__container" style={{ marginTop: "20px" }}>
        <h1>¿A dónde quieres envíar?</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt, 1),
                placeholder: "Salida",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt, 2),
                placeholder: "Destino",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              style={style_input}
              disableToolbar
              variant="inline"
              format="dd/MM/yyyy"
              margin="normal"
              label="Dia del envio"
              value={fecha}
              onChange={handleChangeFecha}
            />
          </MuiPickersUtilsProvider>
          <ToggleButtonGroup
            orientation="vertical"
            value={pesoPaquete}
            exclusive
            onChange={handleChangeToggle}
            style={{ width: "100%", margin: "15px" }}
          >
            <ToggleButton value={1}>
              <div className="selectPaquete">
                <FontAwesomeIcon
                  style={{ fontSize: "2.4rem" }}
                  icon={faEnvelope}
                />
                <p>Sobre | Documento</p>
                <strong>1KG</strong>
              </div>
            </ToggleButton>
            <ToggleButton value={5}>
              <div className="selectPaquete">
                <FontAwesomeIcon
                  style={{ fontSize: "2.4rem" }}
                  icon={faBoxOpen}
                />
                <p>Caja chica</p>
                <strong>2KG - 5KG</strong>
              </div>
            </ToggleButton>
            <ToggleButton value={10}>
              <div className="selectPaquete">
                <FontAwesomeIcon
                  style={{ fontSize: "2.4rem" }}
                  icon={faBoxOpen}
                />
                <p>Caja mediana</p>
                <strong>6KG - 10KG</strong>
              </div>
            </ToggleButton>
            <ToggleButton value={25}>
              <div className="selectPaquete">
                <FontAwesomeIcon
                  style={{ fontSize: "2.4rem" }}
                  icon={faBoxOpen}
                />
                <p>Caja grande</p>
                <strong>11KG - 25KG</strong>
              </div>
            </ToggleButton>
          </ToggleButtonGroup>
          <div className="btnImage">
            <input
              accept="image/*"
              id="imageBtn"
              type="file"
              onChange={handleChangeInputImg}
              style={{ display: "none" }}
            />

            <label htmlFor="imageBtn">
              <Button
                variant="outlined"
                component="span"
                style={{ width: "100%", padding: "10px 0" }}
              >
                <FontAwesomeIcon
                  style={{
                    fontSize: "1.5rem",
                    color: "black",
                    margin: "0 10px",
                  }}
                  icon={faImage}
                />
                <p>Añadir imagen</p>
              </Button>
              {imgPaquete && (
                <img
                  alt="evidencia"
                  className="imgPaquete"
                  ref={refImage}
                  src=""
                ></img>
              )}
            </label>
          </div>
          <FormControlLabel
            style={{ color: "black", marginTop: "15px", marginBottom: "15px" }}
            control={
              <Checkbox
                checked={checkCarta}
                onChange={handleChangeCheck}
                name="checkCarta"
                color="primary"
              />
            }
            label="Carta responsiva"
          />
          <Button
            disabled={!checkCarta}
            style={style_btn}
            type="submit"
            variant="outlined"
          >
            Solicitar Envio
          </Button>
        </form>
        <div>
          <List style={{ width: "100%" }}>
            {viajes.map((viaje) => (
              <ListItem button key={viaje.id}>
                <ListItemAvatar>
                  <Avatar>
                    <FolderIcon />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={
                    <p style={{ textAlign: "center" }}>
                      Hora {viaje.horaViaje} hrs.
                    </p>
                  }
                  secondary={
                    <div style={{ display: "flex" }}>
                      <div style={{ textAlign: "center", margin: "0 8px" }}>
                        <p>{viaje.salida}</p>
                        <p>a</p>
                        <p>{viaje.destino}</p>
                      </div>
                      <div style={{ display: "flex", alignItems: "flex-end" }}>
                        <p>precio</p>
                      </div>
                    </div>
                  }
                />
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    </>
  );
};
