import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { useForm } from "../../hooks/useForm";
import DateFnsUtils from "@date-io/date-fns";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

export const MandaderoScreen = () => {
  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const [{ name, tipoViaje }, handleChange, reset] = useForm({
    name: "",
    tipoViaje: "",
  });

  const [fecha, setFecha] = useState(Date.now());
  const [direccion, setDireccion] = useState("");

  const handleChangeFecha = (fecha) => {
    setFecha(fecha);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const searchOptions = {
    types: ["(cities)"],
    componentRestrictions: {
      country: "mx",
    },
  };

  const handleChangeDireccion = (address) => {
    console.log(address);
    setDireccion(address);
  };

  return (
    <>
      <div className="auth__container">
        <h1>Publicar Viaje</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                placeholder: "Salida",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                placeholder: "Destino",
                getOptionLabel: (option) => {
                  return `${option.value.terms[0].value} ${option.value.terms[1].value}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              style={style_input}
              disableToolbar
              variant="inline"
              format="dd/MM/yyyy"
              margin="normal"
              label="Dia del viaje"
              value={fecha}
              onChange={handleChangeFecha}
            />
          </MuiPickersUtilsProvider>
          <TextField
            type="number"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Equipaje Disponible"
          />
          <FormControl style={style_input}>
            <InputLabel id="tipoViajeLabel">Tipo de Viaje</InputLabel>
            <Select
              labelId="tipoViajeLabel"
              value={tipoViaje}
              onChange={handleChange}
              name="tipoViaje"
            >
              <MenuItem value={"avion"}>Avion</MenuItem>
              <MenuItem value={"coche"}>Coche</MenuItem>
              <MenuItem value={"bus"}>Camión</MenuItem>
            </Select>
          </FormControl>
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Datos del INE"
          />
          <Button style={style_btn} type="submit" variant="outlined">
            Iniciar Sesión
          </Button>
        </form>
      </div>
    </>
  );
};
