import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { useForm } from "../../hooks/useForm";
import DateFnsUtils from "@date-io/date-fns";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import PlacesAutocomplete from "react-places-autocomplete";

export const MandaderoScreen = () => {
  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const [{ name }, handleChange, reset] = useForm({
    name: "",
  });

  const [fecha, setFecha] = useState(Date.now());
  const [direccion, setDireccion] = useState("");

  const handleChangeFecha = (fecha) => {
    setFecha(fecha);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const searchOptions = {
    types: ["(cities)"],
    componentRestrictions: {
      country: "mx",
    },
  };

  const handleChangeDireccion = (address) => {
    setDireccion(address);
  };
  return (
    <>
      <div className="auth__container">
        <h1>Publicar Viaje</h1>
        <form onSubmit={handleSubmit} className="form-login">
          <PlacesAutocomplete
            value={direccion}
            onChange={handleChangeDireccion}
            searchOptions={searchOptions}
          >
            {({
              getInputProps,
              suggestions,
              getSuggestionItemProps,
              loading,
            }) => (
              <div>
                <input
                  {...getInputProps({
                    placeholder: "Search Places ...",
                    className: "location-search-input",
                  })}
                />
                <div className="autocomplete-dropdown-container">
                  {loading && <div>Loading...</div>}
                  {suggestions.map((suggestion) => {
                    console.log(suggestions);
                    const className = suggestion.active
                      ? "suggestion-item--active"
                      : "suggestion-item";
                    // inline style for demonstration purpose
                    const style = suggestion.active
                      ? { backgroundColor: "#fafafa", cursor: "pointer" }
                      : { backgroundColor: "#000000", cursor: "pointer" };
                    return (
                      <div
                        {...getSuggestionItemProps(suggestion, {
                          className,
                          style,
                        })}
                      >
                        <span>`${suggestion[0].value}`</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </PlacesAutocomplete>
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
              style={style_input}
              disableToolbar
              variant="inline"
              format="dd/MM/yyyy"
              margin="normal"
              label="Dia del viaje"
              value={fecha}
              onChange={handleChangeFecha}
            />
          </MuiPickersUtilsProvider>
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
          <TextField
            type="text"
            name="name"
            onChange={handleChange}
            value={name}
            style={style_input}
            label="Nombre Completo"
          />
        </form>
      </div>
    </>
  );
};
