import React from "react";
import { Link } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useForm } from "../../hooks/useForm";
import { useDispatch } from "react-redux";
import { login } from "../../actions/auth";
export const RegisterScreen = () => {
  const handleLogin = () => {
    console.log("login");
  };

  const style_input = {
    margin: "15px 0",
    width: "100%",
  };

  const style_btn = {
    margin: "25px 0",
  };

  const dispatch = useDispatch();

  const [{ correo, password }, handleChange, reset] = useForm({
    correo: "",
    password: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(correo, password);
    dispatch(login(12345, "Adrian"));
    reset();
  };

  return (
    <>
      <h1 className="titulo">¡Únete!</h1>
      <p>Completa el formulario para registrarse</p>

      <form onSubmit={handleSubmit} className="form-login">
        <TextField
          type="text"
          name="displayName"
          onChange={handleChange}
          value={displayName}
          style={style_input}
          id="user"
          label="Nombre Completo"
        />
        <TextField
          type="email"
          name="correo"
          onChange={handleChange}
          value={correo}
          style={style_input}
          id="user"
          label="Correo"
        />
        <TextField
          type="email"
          name="correo"
          onChange={handleChange}
          value={correo}
          style={style_input}
          id="user"
          label="Correo"
        />
        <TextField
          style={style_input}
          onChange={handleChange}
          value={password}
          name="password"
          type="password"
          label="Contraseña"
        />
        <Button style={style_btn} type="submit" variant="outlined">
          Iniciar Sesión
        </Button>
      </form>

      <Button style={{ margin: "10px" }}>
        <Link
          style={{ textDecoration: "none", color: "black" }}
          to="/auth/register"
        >
          ¿No eres cliente? Regístrate aquí
        </Link>
      </Button>
    </>
  );
};
