import React, { useEffect, useState } from "react";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import { TextField, Button } from "@material-ui/core";
import { loadStripe } from "@stripe/stripe-js";
import {
  CardElement,
  Elements,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import { Header } from "../Header";
import { useForm } from "../../../hooks/useForm";
import axios from "axios";
import { useSelector } from "react-redux";
import { CheckoutForm } from "./CheckoutForm";
import { useHistory } from "react-router-dom";

export const CheckoutScreen = () => {
  const [stripePromise, setStripePromise] = useState(() =>
    loadStripe(
      "pk_test_51INZegIUa12FAo8qX1qfFY1QMKJQD8ZiNHxLZY3LjSJRO2XPgR8Ej0oOulhcSiFKHu8srZ0M4GEbCUayXfnSKPpr00qhDNMrpJ"
    )
  );

  const viaje = useSelector((state) => state.busqueda);
  const history = useHistory();

  useEffect(() => {
    if (Object.keys(viaje).length === 0) {
      history.push("/");
    }
  }, [viaje]);

  const searchOptions = {
    componentRestrictions: {
      country: "mx",
    },
  };

  const [{ notas }, handleChange] = useForm({
    notas: "",
  });

  const [direccion, setDireccion] = useState("");

  const handleChangeDireccion = (address) => {
    setDireccion(address);
  };

  return (
    <>
      <Header />
      <div className="auth__container" style={{ marginTop: "20px" }}>
        <h1>Pago</h1>
        <div className="form-login" style={{ color: "black" }}>
          <div style={{ width: "100%", margin: "15px 0" }}>
            <GooglePlacesAutocomplete
              autocompletionRequest={searchOptions}
              selectProps={{
                onChange: (evt) => handleChangeDireccion(evt),
                placeholder: "Direccion complementaria",
                getOptionLabel: (option) => {
                  return `${option.label}`;
                },
                styles: {
                  input: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  option: (provided) => ({
                    ...provided,
                    color: "black",
                  }),
                  singleValue: (provided) => ({ ...provided, color: "black" }),
                },
                loadingMessage: () => "Cargando",
                noOptionsMessage: () => "Sin resultados",
              }}
            />
          </div>

          <div style={{ width: "100%", margin: "20px 0" }}>
            <TextField
              style={{ width: "100%" }}
              label="Notas adicionales"
              multiline
              rowsMax={4}
              name="notas"
              value={notas}
              onChange={handleChange}
            />
          </div>
          <div style={{ width: "100%", margin: "20px 0" }}>
            <Elements stripe={stripePromise}>
              <CheckoutForm
                notas={notas}
                direccion={direccion.label}
                precio={viaje.precio}
              />
            </Elements>
          </div>
        </div>
      </div>
      <Dialog open={abrir} onClose={handleClose}>
        <DialogTitle>{"!Tu envío ha sido registrado con exito!"}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Let Google help apps determine location. This means sending
            anonymous location data to Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Enviar a correo
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};
