import { db } from "../../firebase/firebaseConfig";
import { types } from "../../types/types";

export const viajesBusqueda = (viajes) => ({
  type: types.busqueda,
  payload: viajes,
});

export const viajesBusquedaEmpty = () => ({
  type: types.emptyBusqueda,
});
export const getViaje = ({
  dirSalida,
  dirDestino,
  fechaEnvio,
  pesoPaquete,
}) => {
  return (dispatch) => {
    const viajesRef = db.collection("viajes");
    viajesRef
      .where("salida", "==", dirSalida)
      .where("destino", "==", dirDestino)
      .where("fechaViaje", "==", fechaEnvio)
      .where("equipajePeso", ">=", pesoPaquete)
      .get()
      .then((viajes) => {
        if (!viajes.empty) {
          let viajesData = [];
          viajes.docs.forEach((viaje) => {
            viajesData.push({ ...viaje.data(), id: viaje.id });
          });
          dispatch(viajesBusqueda(viajesData));
        } else {
          dispatch(viajesBusquedaEmpty());
        }
      });
  };
};

const updateViaje = (id, peso) => {
  return db.collection("viajes").doc(id).update({ equipajePeso: peso });
};

const setEnvioViaje = (body) => {
  return db.collection("envios").doc().set(body);
};
