import React, { useRef } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

export const LoginScreen = () => {
  const handleLogin = () => {
    console.log("login");
  };

  const style_input = {
    margin: "15px 0",
    width: "200px",
  };

  const style_btn = {
    margin: "25px 0",
  };

  const [{ correo, password }, handleChange, reset] = useForm({
    correo: "",
    password: "",
  });

  return (
    <div>
      <h1 className="titulo">Iniciar Sesión</h1>
      <form className="form-login">
        <TextField
          type="text"
          name="correo"
          onChange={handleChange}
          value={correo}
          style={style_input}
          id="user"
          label="Nombre de usuario"
        />
        <TextField
          style={style_input}
          type="password"
          id="password"
          label="Contraseña"
        />
        <Button style={style_btn} onClick={handleLogin} variant="outlined">
          Iniciar Sesión
        </Button>
      </form>
    </div>
  );
};
